import React, { Component } from "react";

export default class Loading extends Component {
    state = { seconds: 7 };

    componentDidMount() {
        this.handleTimer();
    }

    componentDidUpdate() {
        this.handleTimer();
    }

    handleTimer = () => {
        const seconds = this.state.seconds;
        if (seconds > 0) {
            setTimeout(() => this.setState({ seconds: seconds - 1 }), 1000);
        } else {
            window.location.reload();
        }
    };

    render() {
        return (
            <div className="mt-3">
                <div className="jumbotron">
                    <h1 className="display-4">Oh, no!</h1>
                    <p className="lead">
                        <strong style={{ fontWeight: "12" }}>
                            We could not reach the backend server.
                        </strong>{" "}
                        Please ensure that the backend server is active when
                        trying to access this webpage. You may refresh this page
                        once the backend server is active or wait for the page
                        to automatically reload.
                    </p>
                    <hr className="my-4" />
                    <div className="d-flex align-items-center">
                        <p>
                            This webpage will automatically be reloaded in{" "}
                            {this.state.seconds} seconds...
                        </p>
                        <div
                            class="spinner-border spinner-border-lg ml-auto"
                            role="status"
                            aria-hidden="true"
                        ></div>
                    </div>
                </div>
            </div>
        );
    }
}
