import React, { Component } from "react";

export default class SongPanel extends Component {
    state = {
        title: "Way 2 Sexy",
        artist: "Drake Featuring Future & Young Thug",
        picture:
            "https://charts-static.billboard.com/img/2021/09/drake-p3d-way-2-sexy-hhn-155x155.jpg",
    };

    render() {
        return (
            <div>
                <div
                    className="card flex-row flex-wrap mx-auto my-auto"
                    style={{ width: "75%", position: "center" }}
                >
                    <div className=" card-header border-0">
                        <img
                            src={this.state.picture}
                            class="card-img-left"
                            alt={this.state.title}
                        />
                    </div>
                    <div className="card-body card-block my-auto">
                        <h3 class="card-title">{this.state.title}</h3>
                        <h5 class="card-text">{this.state.artist}</h5>
                    </div>
                    <div className="my-auto mr-3">
                        <button
                            type="button"
                            className="btn btn-primary btn-lg my-1"
                            style={{ height: "100%" }}
                        >
                            <span className="fa fa-arrow-up fa-lg" />
                        </button>
                        <br />
                        <button
                            type="button"
                            className="btn btn-primary btn-lg my-1"
                        >
                            <span className="fa fa-arrow-down fa-lg" />
                        </button>
                    </div>
                    <div class="w-150"></div>
                </div>
            </div>
        );
    }
}
