import React, { Component } from "react";
import NavBar from "./components/navbar";
import Counters from "./components/counters";
import "./App.css";
import SongDashboard from "./components/songDashboard";

export default class App extends Component {
    state = {
        panels: [
            {
                title: "Way 2 Sexy",
                artist: "Drake Featuring Future & Young Thug",
                picture:
                    "https://charts-static.billboard.com/img/2021/09/drake-p3d-way-2-sexy-hhn-155x155.jpg",
                first: true,
                last: false,
            },
            {
                title: "Bad Habits",
                artist: "Ed Sheeran",
                picture:
                    "https://charts-static.billboard.com/img/2021/07/ed-sheeran-tiy-bad-habits-fce-155x155.jpg",
                first: false,
                last: false,
            },
            {
                title: "Stay",
                artist: "The Kid LAROI & Justin Bieber",
                picture:
                    "https://charts-static.billboard.com/img/2021/07/the-kid-laroi-qev-stay-ens-155x155.jpg",
                first: false,
                last: true,
            },
        ],
    };

    handleMove = (song, up) => {
        let newPanel = [...this.state.panels];
        const idx = newPanel.indexOf(song);
        if (up) {
            newPanel[idx - 1] = { ...song };
            newPanel[idx] = { ...this.state.panels[idx - 1] };
        } else {
            newPanel[idx + 1] = { ...song };
            newPanel[idx] = { ...this.state.panels[idx + 1] };
        }
    };

    handleReset = () => {
        const counters = this.state.counters.map((c) => {
            c.value = 0;
            return c;
        });

        this.setState({ counters });
    };

    render() {
        return (
            <React.Fragment>
                <NavBar />
                <main className="container">
                    <SongDashboard
                        panels={this.state.panels}
                        onMove={this.handleMove}
                    />
                </main>
            </React.Fragment>
        );
    }
}
