import React, { Component } from "react";
import NavBar from "./components/navbar";
import Loading from "./components/loading";
import SongDashboard from "./components/songDashboard";
import axios from "axios";
import "./App.css";

export default class App extends Component {
    state = {
        backendActive: false,
        backendReady: false,
        songDifficulty: 3,
        currScore: 0,
        highScore: 0,
        panels: [
            {
                title: "Way 2 Sexy",
                artist: "Drake Featuring Future & Young Thug",
                picture:
                    "https://charts-static.billboard.com/img/2021/09/drake-p3d-way-2-sexy-hhn-155x155.jpg",
                first: true,
                last: false,
            },
        ],
        songBank: [],
        orderedSongIndices: [],
    };

    handleDifficulty = (delta) => {
        const songDifficulty = this.state.songDifficulty + delta;
        let newPanels = [...this.state.panels];
        if (delta === -1) {
            newPanels.pop();
        } else {
            newPanels.push(
                this.state.songBank[
                    this.state.orderedSongIndices[songDifficulty - 1]
                ]
            );
            newPanels[newPanels.length - 2].last = false;
        }
        newPanels[newPanels.length - 1].last = true;
        this.setState({ songDifficulty });
    };

    handleMove = (song, up) => {
        console.log("Detected");
        let newPanels = [...this.state.panels];
        const idx = newPanels.indexOf(song);

        if (up) {
            newPanels[idx - 1] = { ...song };
            newPanels[idx] = { ...this.state.panels[idx - 1] };
        } else {
            newPanels[idx + 1] = { ...song };
            newPanels[idx] = { ...this.state.panels[idx + 1] };
        }

        newPanels[0].first = true;
        newPanels[1].first = false;
        newPanels[newPanels.length - 1].last = true;
        newPanels[newPanels.length - 2].last = false;

        this.setState({ panels: newPanels });
    };

    handleReset = () => {
        const songDifficulty = 3;
        const currScore = 0;
        const highScore = 0;

        this.setState({ songDifficulty, currScore, highScore });
    };

    handleSongLoad = () => {
        let songIndices = Array(100)
            .fill()
            .map((_, index) => index);
        let orderedSongIndices = this.shuffleIndices(songIndices);

        let panels = [];
        for (let i = 0; i < this.state.songDifficulty; i++) {
            panels.push(this.state.songBank[orderedSongIndices[i]]);
            panels[i].first = i === 0;
            panels[i].last = i === this.state.songDifficulty - 1;
        }
        this.setState({ orderedSongIndices, panels });
    };

    shuffleIndices = (indices) => {
        let currIdx = indices.length,
            randIdx;

        while (currIdx !== 0) {
            randIdx = Math.floor(Math.random() * currIdx);
            currIdx--;

            // And swap it with the current element.
            [indices[currIdx], indices[randIdx]] = [
                indices[randIdx],
                indices[currIdx],
            ];
        }

        return indices;
    };

    constructor() {
        super();
        axios
            .get("http://localhost:8080/")
            .then((res) => {
                if (res.status === 200) {
                    this.setState({
                        backendActive: true,
                        backendReady: true,
                        songBank: res.data,
                    });
                    this.handleSongLoad();
                }
            })
            .catch((error) => {
                if (error.request.status === 0) {
                    // Backend server is not being detected
                    this.setState({
                        backendActive: false,
                        backendReady: false,
                    });
                } else {
                    // Backend server is active but is likely sending back 425, since web scraper is not finished
                    if (error.response.status === 425) {
                        this.setState({
                            backendActive: true,
                            backendReady: false,
                        });
                    } else {
                        this.setState({
                            backendActive: false,
                            backendReady: false,
                        });
                    }
                }
            });
    }

    render() {
        if (!this.state.backendActive || !this.state.backendReady) {
            return (
                <React.Fragment>
                    <NavBar
                        songDifficulty={this.state.songDifficulty}
                        onDifficulty={this.handleDifficulty}
                        currScore={this.state.currScore}
                        highScore={this.state.highScore}
                        onReset={this.handleReset}
                    />
                    <main className="container">
                        <Loading backendActive={this.state.backendActive} />
                    </main>
                </React.Fragment>
            );
        } else {
            return (
                <React.Fragment>
                    <NavBar
                        songDifficulty={this.state.songDifficulty}
                        onDifficulty={this.handleDifficulty}
                        currScore={this.state.currScore}
                        highScore={this.state.highScore}
                        onReset={this.handleReset}
                    />
                    <main className="container">
                        <SongDashboard
                            panels={this.state.panels}
                            onMove={this.handleMove}
                        />
                    </main>
                </React.Fragment>
            );
        }
    }
}
