import React, { Component } from "react";

export default class Loading extends Component {
    state = { seconds: 7 };

    useEffect() {
        console.log("Reached");
    }

    render() {
        return (
            <div className="mt-3">
                <div className="jumbotron">
                    <h1 className="display-4">Oh, no!</h1>
                    <p className="lead">
                        We could not reach the backend server. Please ensure
                        that the backend server is active when trying to access
                        this webpage.
                    </p>
                    <hr className="my-4" />
                    <div className="d-flex align-items-center">
                        <p>
                            <div
                                class="spinner-border spinner-border-lg mr-3"
                                role="status"
                                aria-hidden="true"
                            ></div>
                            This webpage will automatically be reloaded in{" "}
                            {this.state.seconds} seconds...
                        </p>
                    </div>
                </div>
            </div>
        );
    }
}
